mapper.insert:
INSERT INTO t_dm_user
SET idm = #{idm}
, iuser = #{iuser}


    SELECT IFNULL(MAX(seq), 0) + 1
    FROM t_dm_msg
    WHERE idm = #{idm}

INSERT INTO t_dm_msg
SET idm = #{idm}
, seq = #{seq}
, iuser = #{loginedIuser}
, msg = #{msg}

mapper.delete:
delete from t_dm_msg
where idm = #{idm}
and iuser = #{iuser}
and seq = #{seq}
